import "../styles.css";
import { Link } from "react-router-dom";
import { useEffect, useState } from "react";
import CidadePorCep from "../../../servicos/viaCep";
import { toast } from "react-toastify";
import InputSenha from "../../../componentes/inputSenha";
import Logo from "../../componentes/logo";

export default function Cadastro() {
  const [cep, setCep] = useState("");
  const [cidade, setCidade] = useState("");
  const [senhaCadastro, setSenhaCadastro] = useState("");
  const [confirmarSenha, setConfirmarSenha] = useState("");

  async function handlePreencherCidade(meuCep) {
    const cidade = await CidadePorCep(meuCep);

    if (!cidade) {
      toast.error("Falha ao encontrar cidade");
      return;
    }
    setCidade(cidade);
  }

  useEffect(() => {
    if (cep.length < 9 && cidade.length > 0) {
      setCidade("");
    }

    if (cep.indexOf("-") !== -1) {
      if (cep.length === 9) {
        handlePreencherCidade(cep);
      }
      return;
    }

    if (cep.length === 8) {
      handlePreencherCidade(cep);
    }
  }, [cep]);

  return (
    <div>
      <Logo />
      <div className="container-form">
        <form className="form form-cadastro">
          <div className="text-center mb-lg">
            <h1>Cadastre-se</h1>
            <Link to="/login">Entrar</Link>
          </div>
          <div className="body-cadastro">
            <div>
              <div className="flex-column">
                <label>Nome</label>
                <input placeholder="Digite seu nome" type="text" />
              </div>
              <div className="flex-column">
                <label>CEP</label>
                <input
                  placeholder="Digite seu cep"
                  type="text"
                  value={cep}
                  maxLenght={9}
                  onChange={(e) => setCep(e.target.value)}
                />
              </div>
              <InputSenha
                label="Senha"
                placeholder="Digite sua senha"
                value={senhaCadastro}
                setValue={setSenhaCadastro}
              />
            </div>
            <div>
              <div className="flex-column">
                <label>E-mail</label>
                <input placeholder="Digite seu e-mail" type="text" />
              </div>
              <div className="flex-column">
                <label>Cidade</label>
                <input
                  placeholder="Digite seu cidade"
                  type="text"
                  value={cidade}
                  onChange={(e) => setCidade(e.target.value)}
                />
              </div>
              <InputSenha
                label="Confirme sua senha"
                placeholder="Digite sua senha novamente"
                value={confirmarSenha}
                setValue={setConfirmarSenha}
              />
            </div>
          </div>
          <div className="flex-row content-center item-center mb-lg">
            <button className="bt-primario">Enviar</button>
          </div>
        </form>
      </div>
    </div>
  );
}
