import "./styles.css";
import { useState, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import InputSenha from "../../componentes/inputSenha";
import { ReactComponent as ContaLuz } from "../../assets/contaluz.svg";
import Logo from "../../componentes/logo";
import { UseAuth } from "../../contexto/AuthContext";
import { toast } from "react-toastify";
import { useForm } from "react-hook-form";

export default function Login() {
  const [senhaLogin, setSenhaLogin] = useState("");
  const [lembrarUsuario, setLembrarUsuario] = useState(false);
  const { setGravarUsuario, setUsuario, usuario } = UseAuth();
  const history = useHistory();
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  useEffect(() => {
    if (
      errors.email?.type === "required" ||
      errors.senha?.type === "required"
    ) {
      toast.error("Digite email e senha");
    }
  }, [errors.email, errors.senha]);

  async function onSubmit(data) {
    const body = JSON.stringify(data);

    try {
      const response = await fetch("http://localhost:3300/login", {
        method: "POST",
        headers: {
          accept: "application/json",
          "content-type": "application/json",
        },
        body,
      });

      const login = await response.json();

      if (response.status !== 200) {
        toast.error(login);
      } else {
        setUsuario(login);
        if (lembrarUsuario) {
          setGravarUsuario(usuario);
        }
        toast.success(`Olá, ${login.usuario.nome}`, {
          onClose: () => {
            history.push("/fornecedores");
          },
        });
      }
    } catch (error) {
      toast.error(error.message);
    } finally {
    }
  }

  return (
    <div>
      <Logo />
      <div className="container-form-login">
        <div className="conta-luz">
          <ContaLuz />
        </div>
        <form className="form form-login" onSubmit={handleSubmit(onSubmit)}>
          <div className="text-center mb-lg">
            <h1 className="text-blue">Entrar</h1>
            <Link to="/cadastro">Cadastre-se</Link>
          </div>
          <div>
            <div className="flex-column">
              <label className="text-blue" htmlFor="email">
                E-mail
              </label>
              <input
                type="text"
                id="email"
                placeholder="Digite seu e-mail"
                {...register("email", { required: true })}
              ></input>
            </div>
            <InputSenha
              label="Senha"
              name="senha"
              placeholder="Digite sua senha"
              value={senhaLogin}
              setValue={setSenhaLogin}
              {...register("senha", { required: true })}
            />
            <button className="bt-primario" type="submit">
              Enviar
            </button>
            <div className="mt-lg flex-row item-center">
              <input
                type="checkbox"
                value="lembrar-me"
                name="lembrarUsuario"
                checked={lembrarUsuario}
                onChange={() => setLembrarUsuario(!lembrarUsuario)}
              />
              <label className="text-blue" htmlfor="lembrar-me">
                Lembrar-me
              </label>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
}
