import { useStyles } from "../../estilos/backdrop";
import { Backdrop, CircularProgress } from "@material-ui/core";
import { useEffect } from "react";
import { useForm } from "react-hook-form";
import { useHistory } from "react-router-dom";
import { toast } from "react-toastify";
import { UseAuth } from "../../contexto/AuthContext";

export default function Demanda() {
  const { carregando, setCarregando, gravarUsuario } = UseAuth();
  const history = useHistory();
  const classes = useStyles();
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  useEffect(() => {
    if (errors.demanda_kwh?.type === "required") {
      toast.error("Campo obrigatório");
    }
  }, [errors.demanda_kwh]);

  async function onSubmit(data) {
    setCarregando(true);
    const body = JSON.stringify(data);

    try {
      const response = await fetch(`http://localhost:3300/fornecedores`, {
        method: "PATCH",
        headers: {
          accept: "application/json",
          "content-type": "application/json",
          Authorization: "Bearer " + gravarUsuario.token,
        },
        body,
      });

      const demanda = await response.json();

      if (response.status !== 200) {
        toast.error(demanda);
      } else {
        toast.success(demanda, {
          onClose: () => {
            history.push(`/forncedores/${gravarUsuario.usuario.id}`);
          },
        });
      }
    } catch (error) {
      toast.error(error.message);
    } finally {
      setCarregando(false);
    }
  }
  return (
    <div className="container-form">
      <form onSubmit={handleSubmit(onSubmit)}>
        <div className="flex-column">
          <label className="text-blue" htmlFor="demanda_kwh">
            Valor de demanda em KWH
          </label>
          <input
            type="text"
            id="demanda_kwh"
            placeholder="Digite seu valor de demanda"
            {...register("demanda_kwh", { required: true })}
          ></input>
        </div>
      </form>
      <Backdrop className={classes.backdrop} open={carregando}>
        <CircularProgress color="inherit" />
      </Backdrop>
    </div>
  );
}
