import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect,
} from "react-router-dom";
import Login from "./paginas/login";
import Cadastro from "./paginas/cadastro";
import Fornecedores from "./paginas/fornecedores";
import { AuthProvider } from "./contexto/AuthContext";
import { UseAuth } from "./contexto/AuthContext";

function AuthPath(props) {
  const { gravarUsuario, usuario } = UseAuth();
  const token = gravarUsuario.length > 0 ? gravarUsuario.token : usuario.token;

  return (
    <Route render={() => (token ? props.children : <Redirect to="/login" />)} />
  );
}

function Rotas() {
  return (
    <AuthProvider>
      <Router>
        <Switch>
          <Route path="/login" component={Login} />
          <Route path="/cadastro" component={Cadastro} />
          <AuthPath>
            <Route exact path="/" component={Fornecedores} />
          </AuthPath>
        </Switch>
      </Router>
    </AuthProvider>
  );
}

export default Rotas;
