import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEye, faEyeSlash } from "@fortawesome/free-solid-svg-icons";
import "./styles.css";
import { useState, useEffect } from "react";
import { toast } from "react-toastify";
import { useForm } from "react-hook-form";

export default function InputSenha({ label, placeholder, value, setValue }) {
  const [verSenha, setVerSenha] = useState(false);
  const {
    register,
    formState: { errors },
  } = useForm();

  useEffect(() => {
    if (errors.senha?.type === "required") {
      toast.error("Campo senha inválido");
    }
  }, [errors.senha]);

  return (
    <div className="flex-column input-senha">
      <label className="text-blue" htmlFor="password">
        {label}
      </label>
      <input
        type={verSenha ? "text" : "password"}
        id="senha"
        {...register("senha", { required: true })}
        placeholder={placeholder}
        value={value}
        onChange={(e) => setValue(e.target.value)}
      ></input>
      <FontAwesomeIcon
        className="eye-senha"
        onClick={() => setVerSenha(!verSenha)}
        icon={verSenha ? faEye : faEyeSlash}
      />
    </div>
  );
}
