import "./styles.css";
import { useStyles } from "../../estilos/backdrop";
import { Backdrop, CircularProgress } from "@material-ui/core";
import { useEffect } from "react";
import { useForm } from "react-hook-form";
import { toast } from "react-toastify";
import { UseAuth } from "../../contexto/AuthContext";
import { useState } from "react";
import { useHistory } from "react-router-dom";
import { ReactComponent as ProcurarDemanda } from "../../assets/contalupa.svg";
import Logo from "../../componentes/logo";
import Card from "../../componentes/card-fornecedores";

export default function Fornecedores() {
  const { carregando, setCarregando, token } = UseAuth();
  const [fornecedores, setFornecedores] = useState([]);
  const classes = useStyles();
  const history = useHistory();
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  useEffect(() => {
    if (errors.demanda_kwh?.type === "required") {
      toast.error("Campo obrigatório");
    }
  }, [errors.demanda_kwh]);

  useEffect(() => {
    if (!token) {
      setFornecedores([]);
    }
  }, [token]);

  async function onSubmit(data) {
    setCarregando(true);
    const valorDemanda = data.demanda_kwh;

    try {
      const response = await fetch(
        `http://localhost:3300/?demanda_kwh=${valorDemanda}`,
        {
          method: "GET",
          headers: {
            accept: "application/json",
            "content-type": "application/json",
            Authorization: "Bearer " + token,
          },
        }
      );

      const demanda = await response.json();

      if (response.status !== 200) {
        toast.error(demanda);
      } else {
        setFornecedores(demanda);
        history.push(`/?demanda_kwh=${valorDemanda}`);
      }
    } catch (error) {
      toast.error(error.message);
    } finally {
      setCarregando(false);
    }
  }
  return (
    <div>
      <Logo />
      <div className="container-form flex-column">
        <form className="flex-row busca" onSubmit={handleSubmit(onSubmit)}>
          <div className="flex-column">
            <label className="text-blue" htmlFor="demanda_kwh">
              Valor de demanda em KWH
            </label>
            <input
              type="text"
              id="demanda_kwh"
              placeholder="Digite seu valor de demanda"
              {...register("demanda_kwh", { required: true })}
            ></input>
            <button className="bt-primario" type="submit">
              BUSCAR
            </button>
          </div>
          <div className="imagem-busca">
            <ProcurarDemanda />
          </div>
        </form>
        <div className="card">
          {Array.isArray(fornecedores)
            ? fornecedores.map((fornecedor) => (
                <Card
                  nome={fornecedor.nome}
                  logo={fornecedor.logo}
                  estado={fornecedor.estado}
                  custo={fornecedor.custo_kwh}
                  clientes={fornecedor.qtd_clientes}
                  avaliacao={fornecedor.avaliacao_media}
                />
              ))
            : fornecedores}
        </div>
      </div>
      <Backdrop className={classes.backdrop} open={carregando}>
        <CircularProgress color="inherit" />
      </Backdrop>
    </div>
  );
}
