import "./styles.css";
import { useStyles } from "../../estilos/backdrop";
import { Backdrop, CircularProgress } from "@material-ui/core";
import { Link, useHistory } from "react-router-dom";
import { useEffect, useState } from "react";
import { toast } from "react-toastify";
import { useForm } from "react-hook-form";
import { UseAuth } from "../../contexto/AuthContext";
import CidadePorCep from "../../servicos/viacep";
import InputSenha from "../../componentes/inputSenha";
import Logo from "../../componentes/logo";

export default function Cadastro() {
  const [cep, setCep] = useState("");
  const [cidade, setCidade] = useState("");
  const [senhaCadastro, setSenhaCadastro] = useState("");
  const [confirmarSenha, setConfirmarSenha] = useState("");
  const { carregando, setCarregando } = UseAuth();
  const history = useHistory();
  const classes = useStyles();
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  async function handlePreencherCidade(meuCep) {
    const cidade = await CidadePorCep(meuCep);

    if (!cidade) {
      toast.error("Falha ao encontrar cidade");
      return;
    }
    setCidade(cidade);
  }

  useEffect(() => {
    if (cep.length < 9 && cidade.length > 0) {
      setCidade("");
    }

    if (cep.indexOf("-") !== -1) {
      if (cep.length === 9) {
        handlePreencherCidade(cep);
      }
      return;
    }

    if (cep.length === 8) {
      handlePreencherCidade(cep);
    }
  }, [cep]);

  useEffect(() => {
    if (
      errors.email?.type === "required" ||
      errors.nome?.type === "required" ||
      errors.cep?.type === "required" ||
      errors.cidade?.type === "required" ||
      errors.senha?.type === "required" ||
      errors.confirmar_senha?.type === "required"
    ) {
      toast.error("Todos os campos são obrigatórios");
    }
  }, [
    errors.email,
    errors.senha,
    errors.nome,
    errors.cep,
    errors.cidade,
    errors.confirmar_senha,
  ]);

  async function onSubmit(data) {
    if (data.senha !== data.confirmar_senha) {
      toast.error("As senhas devem ser iguais");
      return;
    }
    console.log(data);
    // setCarregando(true);
    // const { confirmar_senha, ...dataRequerida } = data;
    // const body = JSON.stringify(dataRequerida);

    // try {
    //   const response = await fetch("http://localhost:3300/cadastro", {
    //     method: "POST",
    //     headers: {
    //       accept: "application/json",
    //       "content-type": "application/json",
    //     },
    //     body,
    //   });

    //   const cadastro = await response.json();

    //   if (response.status !== 200) {
    //     toast.error(cadastro);
    //   } else {
    //     toast.success(cadastro, {
    //       onClose: () => {
    //         history.push("/login");
    //       },
    //     });
    //   }
    // } catch (error) {
    //   toast.error(error.message);
    // } finally {
    //   setCarregando(false);
    // }
  }

  return (
    <div>
      <Logo />
      <div className="container-form">
        <form className="form form-cadastro" onSubmit={handleSubmit(onSubmit)}>
          <div className="text-center mb-lg">
            <h1 className="text-blue">Cadastre-se</h1>
            <Link to="/login">Entrar</Link>
          </div>
          <div className="body-cadastro">
            <div>
              <div className="flex-column">
                <label className="text-blue">Nome</label>
                <input
                  placeholder="Digite seu nome"
                  type="text"
                  {...register("nome", { required: true })}
                />
              </div>
              <div className="flex-column">
                <label className="text-blue">CEP</label>
                <input
                  placeholder="Digite seu cep"
                  type="text"
                  {...register("cep", { required: true })}
                  value={cep}
                  maxLenght={9}
                  onChange={(e) => setCep(e.target.value)}
                />
              </div>
              <InputSenha
                label="Senha"
                placeholder="Digite sua senha"
                value={senhaCadastro}
                setValue={setSenhaCadastro}
                {...register("senha", { required: true })}
              />
            </div>
            <div>
              <div className="flex-column">
                <label className="text-blue">E-mail</label>
                <input
                  placeholder="Digite seu e-mail"
                  type="text"
                  {...register("email", { required: true })}
                />
              </div>
              <div className="flex-column">
                <label className="text-blue">Cidade</label>
                <input
                  placeholder="Digite seu cidade"
                  {...register("cidade", { required: true })}
                  type="text"
                  value={cidade}
                />
              </div>
              <InputSenha
                label="Confirme sua senha"
                placeholder="Digite sua senha novamente"
                value={confirmarSenha}
                setValue={setConfirmarSenha}
                {...register("confirmar_senha", { required: true })}
              />
            </div>
          </div>
          <div className="flex-row content-center item-center mb-lg">
            <button className="bt-primario">Enviar</button>
          </div>
        </form>
      </div>
      <Backdrop className={classes.backdrop} open={carregando}>
        <CircularProgress color="inherit" />
      </Backdrop>
    </div>
  );
}
