import "./styles.css";
import { ReactComponent as LogoCE } from "../../assets/logo.svg";
import { UseAuth } from "../../contexto/AuthContext";
import { Link, useHistory } from "react-router-dom";

export default function Logo() {
  const { gravarUsuario, removeGravarUsuario, usuario } = UseAuth();
  const history = useHistory();
  const token = gravarUsuario.token ?? usuario.token;

  function deslogar() {
    if (gravarUsuario) {
      removeGravarUsuario();
    }

    history.push("/login");
    return;
  }

  return (
    <div className="logo">
      <LogoCE />
      {token && (
        <div className="links">
          <Link path="/login" onClick={() => deslogar()}>
            Sair
          </Link>
          <a
            target="_blank"
            href="https://clarke.com.br/simulador/"
            rel="noreferrer"
          >
            Simulador
          </a>
          <a
            target="_blank"
            href="https://clarke.com.br/blog/"
            rel="noreferrer"
          >
            Blog
          </a>
        </div>
      )}
    </div>
  );
}
